package set4; public class Node<Object> { protected Object data; protected Node<Object> n, p; public Node() { n = null; p = null; data = null; } public Node(Object d, Node<Object> n, Node<Object> p) { data = d; this.n = n; this.p = p; } public void setLinkNext(Node<Object> n) { this.n = n; } public void setLinkPrev(Node<Object> p) { this.p = p; } public Node<Object> getLinkNext() { return this.n; } public Node<Object> getLinkPrev() { return this.p; } public void setData(Object d) { data = d; } public Object getData() { return data; } }
